$(document).ready(function(){    $("form.ajax").live('submit', function (e) {        $(this).ajaxSubmit();        e.preventDefault();    });    /*    $("form :submit").live('click', function () {                $(this).ajaxSubmit();                return false;        });    */    $("a.ajax").live("click", function (event) {        event.preventDefault();        $.get(this.href);    });    $("div.flash").livequery(function () {                var el = $(this);                var left = $(document).width()/2 - el.width()/2;                el.css('left',left );                setTimeout(function () {                        el.animate({"opacity": 0}, 1000);                        el.slideUp();                }, 2000);        });});